mapper.select:
select answerID, content, childrenID, questionID, isUsed, createdAt
from answer
where childrenID = #{childrenID}
order by createdAt;

select answerID, content, childrenID, questionID, isUsed, createdAt
from answer
where childrenID = #{childrenID} and questionID = #{questionID}
order by createdAt;

mapper.update:
update answer
set content = #{content}, createdAt = now()
where answerID = #{answerID}
